#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\prana\Desktop\Ai Content Generator\JCI-PONDA\node_modules\yaml\node_modules;C:\Users\prana\Desktop\Ai Content Generator\JCI-PONDA\node_modules;C:\Users\prana\Desktop\Ai Content Generator\node_modules;C:\Users\prana\Desktop\node_modules;C:\Users\prana\node_modules;C:\Users\node_modules;C:\node_modules;C:\Users\prana\Desktop\Ai Content Generator\JCI-PONDA\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/prana/Desktop/Ai Content Generator/JCI-PONDA/node_modules/yaml/node_modules:/mnt/c/Users/prana/Desktop/Ai Content Generator/JCI-PONDA/node_modules:/mnt/c/Users/prana/Desktop/Ai Content Generator/node_modules:/mnt/c/Users/prana/Desktop/node_modules:/mnt/c/Users/prana/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/prana/Desktop/Ai Content Generator/JCI-PONDA/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../yaml/bin.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../yaml/bin.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../yaml/bin.mjs" $args
  } else {
    & "node$exe"  "$basedir/../yaml/bin.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
